INSERT INTO [Categories] VALUES
	(N'Pháp Lý', N'9-20x20.png', N'banner-3.jpg', N'Nơi tự hào là địa chỉ uy tín và chất lượng hàng đầu dành cho những ai đam mê tìm hiểu tra cứu các văn bản  luật, pháp lý và mong muốn tìm kiếm những cuốn sách về lĩnh vực luật - văn bản luật cùng các tác giả nổi tiếng trong lĩnh vực pháp lý.', CAST(N'2021-09-20T19:09:20.993' AS DateTime), 1, NULL, 0, CAST(N'2021-11-12T14:59:21.427' AS DateTime), 1, N'phap-ly'),
	(N'Kinh Tế', N'5-20x20.png', N'az-viet-nam-banner.jpg', N'Nền kinh tế trên toàn thế giới đang bị tác động mạnh mẽ bởi cuộc cách mạng Công nghiệp 4.0. Nhiều khái niệm, định luật, lĩnh vực không còn phù hợp đã bị đào thải nhanh chóng để bắt kịp với sự vận động liên tục của nhân loại.', CAST(N'2022-09-20T19:09:20.993' AS DateTime), 1, NULL, 0, CAST(N'2022-11-12T14:59:21.427' AS DateTime), 1, N'kinh-te'),
	(N'Văn Hóa Xã Hội', N'2-20x20.png', N'banner-3.jpg', N'Khả năng sáng tạo của con người là vô hạn, chúng ta hoàn toàn có thể tạo nên những trải nghiệm cho chính mình bằng sức mạnh niềm tin, của suy nghĩ và cảm xúc. Chúng ta có thể lựa chọn niềm tin và từ đó làm thay đổi thực tế cuộc sống của mình, tạo nên tất cả những gì mà mình hằng mơ ước.', CAST(N'2022-09-21T20:18:06.043' AS DateTime), 1, NULL, 0, CAST(N'2022-10-12T19:56:49.137' AS DateTime), 1, N'van-hoa-xa-hoi'),
	(N'Văn Học Nghệ Thuật', N'6-20x20.png', N'banner-3.jpg', N'Văn học nghệ thuật (còn gọi là văn học tinh hoa) là dòng văn học cao cấp, do tầng lớp tri thức của xã hội sáng tạo nên với nhu cầu được khám phá nội tâm và đời sống tinh thần của con người, giúp người đọc hiểu được những phức cảm tâm lý sâu sắc của con người, như khát vọng được sống, được tự do, hoặc những mặc cảm tội...', CAST(N'2023-09-21T20:19:40.737' AS DateTime), 1, NULL, 0, CAST(N'2023-10-12T19:57:36.573' AS DateTime), 1, N'van-hoc-nghe-thuat'),
	(N'Giáo Trình', N'11-20x20.png', N'banner-3.jpg', N'Giáo trình là hệ thống chương trình giảng dạy của một môn học. Nó là tài liệu học tập hoặc giảng dạy được thiết kế và biên soạn dựa trên cơ sở chương trình môn học. Mục đích để làm tài liệu giảng dạy chính thức cho giáo viên, hoặc / và làm tài liệu học tập chính thức cho học sinh, sinh viên.', CAST(N'2023-07-21T20:22:23.947' AS DateTime), 1, NULL, 0, CAST(N'2023-10-12T19:57:48.607' AS DateTime), 1, N'giao-trinh'),
	(N'Tiểu Thuyết', N'4-20x20.png', N'alpha-book-banner.jpg', N'Tiểu thuyết là một thể loại văn xuôi có hư cấu, thông qua nhân vật, hoàn cảnh, sự việc để phản ánh bức tranh xã hội rộng lớn và những vấn đề của cuộc sống con người, biểu hiện tính chất tường thuật, tính chất kể chuyện bằng ngôn ngữ văn xuôi theo những chủ đề xác định.', CAST(N'2023-08-21T20:24:22.287' AS DateTime), 1, NULL, 0, CAST(N'2023-10-20T22:27:04.633' AS DateTime), 1, N'light-novel'),
	(N'Tâm Linh', N'10-20x20.png', N'banner-3.jpg', N'Sách Tâm Linh là một thể loại sách có liên quan đến tâm lý, tình cảm và hành động của con người. Ngoài ra tìm đọc một cuốn sách tâm linh hay còn giúp cho chúng ta hiểu thêm về bản thân mình và các mối quan hệ xã hội xung quanh. Từ đây giúp chúng ta giải quyết được các vấn đề trong cuộc sống. ', CAST(N'2022-08-22T06:02:54.557' AS DateTime), 1, NULL, 0, CAST(N'2023-09-22T10:01:44.787' AS DateTime), 1, N'tam-linh'),
	(N'Thiếu Nhi', N'10-20x20.png', N'nxb-kim-dong-banner.jpg', N'Một nhà văn viết về văn học thiếu nhi định nghĩa nó là "tất cả các cuốn sách viết cho trẻ em, ngoại trừ các tác phẩm như truyện tranh, truyện cười, sách hoạt hình và các tác phẩm phi hư cấu không được đọc từ trước ra sau, như từ điển, bách khoa toàn thư và các tài liệu tham khảo khác."', CAST(N'2023-08-22T06:03:43.933' AS DateTime), 1, NULL, 0, CAST(N'2023-10-29T22:27:50.997' AS DateTime), 1, N'thieu-nhi')